/** @format */

import React, { useState } from "react";
import withFirebaseAuth from "react-with-firebase-auth";
import firebase from "firebase/app";
import "firebase/auth";
import firebaseApp from "../../firebase/firebase";
import { useDispatch } from "react-redux";
import { setSignIn } from "../../slices/authenticate.slice";
import {
  Alert,
  Container,
  Row,
  Col,
  Card,
  Form,
  Button,
} from "react-bootstrap";
import { authenticate } from "../../slices/authenticate.slice";

const SignUp = (props) => {
  const { signInWithEmailAndPassword, signInWithGoogle, user } = props;
  const [credentials, setCredentials] = useState({
    email: "",
    pass: "",
  });

  const handleChange = (event) => {
    const { value, name } = event.target;
    setCredentials({ ...credentials, [name]: value });
  };
  const handleSubmit = (event) => {
    event.preventDefault();
    signInWithEmailAndPassword(credentials.email, credentials.pass);
  };
  const dispatch = useDispatch();
  if (user) {
    dispatch(authenticate());
  }
  return (

          <Card>
            <Card.Header style={{ textAlign: "center" }} as="h5">
              Sign In
            </Card.Header>
            <Card.Body>
              <Form onSubmit={handleSubmit}>
                <Form.Group controlId="formBasicEmail">
                  <Form.Label>Email address</Form.Label>
                  <Form.Control
                    onChange={handleChange}
                    name="email"
                    type="email"
                    placeholder="Enter email"
                  />
                </Form.Group>

                <Form.Group controlId="formBasicPassword">
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    onChange={handleChange}
                    name="pass"
                    type="password"
                    placeholder="Password"
                  />
                </Form.Group>

                <Button
                  className="mt-3"
                  block
                  variant="outline-primary"
                  type="submit"
                >
                  Sign In
                </Button>
                <Button
                  onClick={signInWithGoogle}
                  className="mt-3"
                  block
                  variant="outline-secondary"
                >
                  Continue with Google
                </Button>
                <Button
                  onClick={() => {
                    dispatch(setSignIn());
                  }}
                  block
                  variant="link"
                >
                  Don't have an account? Sign up instead
                </Button>
              </Form>
            </Card.Body>
          </Card>

  );
};

const firebaseAppAuth = firebaseApp.auth();

const providers = {
  googleProvider: new firebase.auth.GoogleAuthProvider(),
};

export default withFirebaseAuth({
  providers,
  firebaseAppAuth,
})(SignUp);
