/** @format */

import React, { useState } from "react";
import withFirebaseAuth from "react-with-firebase-auth";
import firebase from "firebase/app";
import "firebase/auth";
import firebaseApp from "./firebase/firebase";
import Header from "./components/header/header.component";
import Body from "./components/body/body.component";
import { useEffect } from "react";

import { loadRecipes } from "./slices/recipe.slice";
import { setUserId } from "./slices/authenticate.slice";
import { useDispatch } from "react-redux";

const App = (props) => {
  const { user } = props;
  const dispatch = useDispatch();

  const getImageUrl = (url) => {
    let imageUrl = ""

    var storage = firebaseApp.storage();
    storage
      .ref()
      .child(items[item].imageUrl)
      .getDownloadURL()
      .then((url) => {
        return url
      });

  };

  useEffect(() => {
    // Update the document title using the browser API
    if (user) {
      dispatch(setUserId(user.uid));
      const itemsRef = firebaseApp
        .database()
        .ref("users")
        .child(user.uid)
        .child("items");
      itemsRef.on("value", (snapshot) => {
        let items = snapshot.val();
        let newState = [];

        for (let item in items) {
          let imageUrl;
          if (items[item].imageUrl.includes("firebase")) {
            storage
              .ref()
              .child(items[item].imageUrl)
              .getDownloadURL()
              .then((url) => {
                imageUrl = url;
                console.log(imageUrl);
              });
          } else {
            imageUrl = items[item].imageUrl;
          }
          newState.push({
            id: item,
            title: items[item].title,
            imageUrl: imageUrl,
            description: items[item].description,
            ingredients: items[item].ingredients,
            directions: items[item].directions,
          });
        }
        dispatch(loadRecipes(newState));
      });
    }
  });
  return (
    <div style={{ overflowX: "hidden" }}>
      <Header />
      <Body />
    </div>
  );
};

const firebaseAppAuth = firebaseApp.auth();

const providers = {
  googleProvider: new firebase.auth.GoogleAuthProvider(),
};

export default withFirebaseAuth({
  providers,
  firebaseAppAuth,
})(App);
