/** @format */

import React from "react";
import firebase from "../../firebase/firebase";
import { useDispatch } from "react-redux";
import { openRecipe } from "../../slices/recipe.slice";
import { Card, Button, Dropdown, ButtonGroup } from "react-bootstrap";
import {
  ButtonContainer,
  TitleContainer,
  ImageContainer,
} from "./saved-recipe-card.styles";
import { MoreVert } from "@material-ui/icons";

const SavedRecipeCard = (props) => {
  const dispatch = useDispatch();
  const itemRef = firebase.database().ref(`/items/${props.id}`);
  return (
    <Card>
      <ImageContainer>
        <Card.Img
          style={{ height: "20rem", objectFit: "cover" }}
          variant="top"
          src={props.imageUrl}
        />
      </ImageContainer>
      <Card.Body>
        <Card.Title>{props.title}</Card.Title>
        <ButtonContainer>
          <Dropdown as={ButtonGroup} style={{ width: "100%" }}>
            <Dropdown.Toggle
              style={{
                borderRadius: "100%",
                padding: ".25rem .65rem",
              }}
              variant="light"
              size="sm"
            ></Dropdown.Toggle>
            <Dropdown.Menu>
              <Dropdown.Item
                onClick={() => {
                  itemRef.remove();
                }}
              >
                Delete Recipe
              </Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>
        </ButtonContainer>
        <Card.Text>{props.description}</Card.Text>

        <Button
          style={{ width: "100%" }}
          variant="outline-primary"
          onClick={() => {
            dispatch(openRecipe(props));
          }}
        >
          Open Recipe
        </Button>
      </Card.Body>
    </Card>
  );
};

export default SavedRecipeCard;
