/** @format */

import React from "react";
import { useDispatch } from "react-redux";
import { activateForm } from "../../slices/form.slice";
import CreateButton from "../create-button/create-button.component";
import {
  IntroContainer,
  NewRecipeContainer,
  ButtonContainer,
} from "./getting-started.styles";

const GettingStarted = () => {
  const dispatch = useDispatch();
  return (
    <Card className="text-center">
      <Card.Header>Getting Started</Card.Header>
      <Card.Body>
        <Card.Title>Create New Recipe</Card.Title>
        <Card.Text>
          With supporting text below as a natural lead-in to additional content.
        </Card.Text>
        <Button variant="outline-primary">Go somewhere</Button>
        <Button variant="outline-secondary">Go somewhere</Button>
      </Card.Body>
    </Card>
  );
};

export default GettingStarted;

<DropdownButton
menuAlign="right"
variant="outline-primary"
id="dropdown-basic-button"
title="Create Recipe"
>
<Dropdown.Item
  onClick={() => {
    dispatch(activateForm());
    dispatch(activateUrl());
  }}
>
  From allrecipes.com
</Dropdown.Item>
<Dropdown.Item
  onClick={() => {
    dispatch(activateForm());
    dispatch(deactivateUrl());
  }}
>
  From scratch
</Dropdown.Item>
</DropdownButton>
