/** @format */

import React, { useState } from "react";
import Header from "./components/header/header.component";
import Body from "./components/body/body.component";
import { useEffect } from "react";
import firebase from "./firebase/firebase";
import { loadRecipes } from "./slices/recipe.slice";
import { useDispatch } from "react-redux";

const App = () => {
  const dispatch = useDispatch(loadRecipes);
  useEffect(() => {
    // Update the document title using the browser API
    const itemsRef = firebase.database().ref("items");
    itemsRef.on("value", (snapshot) => {
      let items = snapshot.val();
      let newState = [];
      for (let item in items) {
        newState.push({
          id: item,
          title: items[item].title,
          imageUrl: items[item].imageUrl,
          description: items[item].description,
          ingredients: items[item].ingredients,
          directions: items[item].directions,
        });
      }
      dispatch(loadRecipes(newState));
    });
  });
  return (
    <div>
      <Header />
      <Body style={{backgroundImage: `url("https://images.unsplash.com/photo-1466637574441-749b8f19452f?ixlib=rb-1.2.1&ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&auto=format&fit=crop&w=2428&q=80")`,}}/>
    </div>
  );
};

export default App;
