/** @format */

import React from "react";

import { useSelector } from "react-redux";
import {
  selectIsSignIn,
  selectResetPassword,
} from "../../slices/authenticate.slice";
import { Card, Container, Row, Col, Image } from "react-bootstrap";
import SignIn from "../sign-in/sign-in.component";
import SignUp from "../sign-up/sign-up.component";
import ResetPassword from "../reset-password/reset-password.component";
import image from "../../assets/breakfast.jpg";

const Authenticate = () => {
  const isSignIn = useSelector(selectIsSignIn);
  const resetPassword = useSelector(selectResetPassword);

  return (
    <Container
      style={{
        marginTop: "125px",
      }}
    >
      <Row className="justify-content-center">
        <Col style={{ marginBottom: "2rem" }} xs={11} sm={10} md={8} lg={6}>
          <Card>
            <h3
              style={{
                color: "white",
                textAlign: "center",
                position: "absolute",
                top: "45%",
                left: "13%",
              }}
            >
              Create your own recipe collection
            </h3>
            <Card.Img style={{opacity: "0.5"}} variant="top" src={image} />
          </Card>
        </Col>
        <Col class="mt-1" xs={12} sm={10} md={8} lg={5} xl={4}>
          {!isSignIn ? (
            <SignUp />
          ) : !resetPassword ? (
            <SignIn />
          ) : (
            <ResetPassword />
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default Authenticate;
