/** @format */

import React from "react";
import firebase from "../../firebase/firebase";
import { useDispatch } from "react-redux";
import { openRecipe } from "../../slices/recipe.slice";
import { Card, Button, Dropdown, ButtonGroup } from "react-bootstrap";
import { ButtonContainer, ImageContainer } from "./saved-recipe-card.styles";
import { MoreVert } from "@material-ui/icons";

const SavedRecipeCard = (props) => {
  const dispatch = useDispatch();
  const itemRef = firebase.database().ref(`/items/${props.id}`);
  return (
    <Card>
      <ImageContainer>
        <ButtonContainer>
          <Dropdown class="btn btn-info btn-sm"as={ButtonGroup} style={{ width: "100%" }}>
            <Dropdown.Toggle
              style={{ padding: ".25rem .25rem", opacity: "0.75" }}
              variant="info"
              size="sm"
            >
              <MoreVert style={{ padding: "0" }} />
            </Dropdown.Toggle>
            <Dropdown.Menu>
              <Dropdown.Item
                onClick={() => {
                  itemRef.remove();
                }}
              >
                Delete Recipe
              </Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>
        </ButtonContainer>
        <Card.Img variant="top" src={props.imageUrl} />
      </ImageContainer>
      <Card.Body>
        <Card.Title>{props.title}</Card.Title>
        <Card.Text>{props.description}</Card.Text>

        <Dropdown drop="up" as={ButtonGroup} style={{ width: "100%" }}>
          <Button
            style={{ width: "95%" }}
            variant="outline-primary"
            onClick={() => {
              dispatch(openRecipe(props));
            }}
          >
            Open Recipe
          </Button>

          <Dropdown.Toggle
            split
            variant="outline-primary"
            id="dropdown-split-basic"
          />
          <Dropdown.Menu>
            <Dropdown.Item
              onClick={() => {
                itemRef.remove();
              }}
            >
              Delete Recipe
            </Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      </Card.Body>
    </Card>
  );
};

export default SavedRecipeCard;
