/** @format */

import React, { useState } from "react";
import withFirebaseAuth from "react-with-firebase-auth";
import firebase from "firebase/app";
import "firebase/auth";
import firebaseApp from "../../firebase/firebase";
import { useDispatch } from "react-redux";
import { setSignIn } from "../../slices/authenticate.slice";
import {
  Alert,
  Container,
  Row,
  Col,
  Card,
  Form,
  Button,
} from "react-bootstrap";

const SignUp = (props) => {
  const { signUp, signInWithGoogle } = props;
  const [credentials, setCredentials] = useState({
    email: "",
    pass: "",
    confirmPass: "",
  });
  const [doesNotMatch, setDoesNotMatch] = useState(false);
  if (doesNotMatch) {
    setTimeout(() => {
      setDoesNotMatch(false);
    }, 1750);
  }
  const handleChange = (event) => {
    const { value, name } = event.target;
    setCredentials({ ...credentials, [name]: value });
  };
  const handleSubmit = (event) => {
    event.preventDefault()
    if (credentials.pass !== credentials.confirmPass) {
      setDoesNotMatch(true);
    } else {
      signUp({ email: credentials.email, password: credentials.pass });
    }
  };
  const dispatch = useDispatch();
  return (
    <Container>
      <Row className="justify-content-center">
        <Col md={6}>
          <Card>
            <Card.Header style={{ textAlign: "center" }} as="h5">
              Sign Up
            </Card.Header>
            <Card.Body>
              <Form onSubmit={handleSubmit}>
                <Form.Group controlId="formBasicEmail">
                  <Form.Label>Email address</Form.Label>
                  <Form.Control
                    onChange={handleChange}
                    name="email"
                    type="email"
                    placeholder="Enter email"
                  />
                </Form.Group>

                <Form.Group controlId="formBasicPassword">
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    onChange={handleChange}
                    name="pass"
                    type="password"
                    placeholder="Password"
                  />
                </Form.Group>

                <Form.Group controlId="formBasicPassword">
                  <Form.Label>Confirm password</Form.Label>
                  <Form.Control
                    onChange={handleChange}
                    name="confirmPass"
                    type="password"
                    placeholder="Password"
                  />
                </Form.Group>
                {doesNotMatch ? (
                  <Alert variant="warning">The passwords do not match</Alert>
                ) : null}

                <Button
                  className="mt-3"
                  block
                  variant="outline-primary"
                  type="submit"
                >
                  Sign Up
                </Button>
                <Button
                  onClick={signInWithGoogle}
                  className="mt-3"
                  block
                  variant="outline-secondary"
                >
                  Sign Up with Google
                </Button>
                <Button
                  onClick={() => {
                    dispatch(setSignIn());
                  }}
                  block
                  variant="link"
                >
                  Already have an account? Sign in instead
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

const firebaseAppAuth = firebaseApp.auth();

const providers = {
  googleProvider: new firebase.auth.GoogleAuthProvider(),
};

export default withFirebaseAuth({
  providers,
  firebaseAppAuth,
})(SignUp);
