/** @format */

import React from "react";
import withFirebaseAuth from "react-with-firebase-auth";
import firebase from "firebase/app";
import "firebase/auth";
import firebaseApp from "./firebase/firebase";
import { authenticate } from "./slices/authenticate.slice";
import Header from "./components/header/header.component";
import Body from "./components/body/body.component";
import { useEffect } from "react";
import { loadRecipes } from "./slices/recipe.slice";
import { useDispatch } from "react-redux";

const App = (props) => {
  const { signInWithGoogle } = props;
  const dispatch = useDispatch(loadRecipes);
  useEffect(() => {
    // Update the document title using the browser API
    const itemsRef = firebaseApp.database().ref("items");
    itemsRef.on("value", (snapshot) => {
      let items = snapshot.val();
      let newState = [];
      for (let item in items) {
        newState.push({
          id: item,
          title: items[item].title,
          imageUrl: items[item].imageUrl,
          description: items[item].description,
          ingredients: items[item].ingredients,
          directions: items[item].directions,
        });
      }
      dispatch(loadRecipes(newState));
    });
  });
  return (
    <div>
      <Header />
      <Body />
    </div>
  );
};

const firebaseAppAuth = firebaseApp.auth();

const providers = {
  googleProvider: new firebase.auth.GoogleAuthProvider(),
};

export default withFirebaseAuth({
  providers,
  firebaseAppAuth,
})(App);
