/** @format */

import { createSlice } from "@reduxjs/toolkit";

export const urlSlice = createSlice({
  name: "url",
  initialState: {
    url: "",
    recipe: "",
  },
  reducers: {
    setUrl: (state, action) => {
      state.url = action.payload.value;
    },
    submitUrl: (state, action) => {
      fetch("/recipe", {
        method: "POST", // or 'PUT'
        headers: {
          "Content-Type": "application/json",
        },
        body: state.url,
      })
        .then((res) => res.json())
        .then((data) => {
          state.recipe = JSON.stringify(data);
        });
    },
  },
});

export const { setUrl, submitUrl } = urlSlice.actions;

// The function below is called a selector and allows us to select a value from
// the state. Selectors can also be defined inline where they're used instead of
// in the slice file. For example: `useSelector((state) => state.counter.value)`
export const selectRecipe = (state) => state.url.url;
export const selectUrl = (state) => state.url.url;

export default urlSlice.reducer;
