/** @format */

import React from "react";
import withFirebaseAuth from "react-with-firebase-auth";
import firebase from "firebase/app";
import "firebase/auth";
import firebaseApp from "../../firebase/firebase";
import { useSelector, useDispatch } from "react-redux";
import { deauthenticate, setSignIn } from "../../slices/authenticate.slice";
import { selectIsAuthenticated } from "../../slices/authenticate.slice";
import { Button, Navbar } from "react-bootstrap";
import CreateButton from "../create-button/create-button.component";

const Header = (props) => {
  const dispatch = useDispatch();
  const { signOut, user } = props;
  if (!user) {
    dispatch(deauthenticate());
  }
  const isAuthenticated = useSelector(selectIsAuthenticated);
  return (
    <Navbar
      fixed="top"
      className="justify-content-between"
      bg="light"
      expand="lg"
    >
      <Navbar.Brand href="#home">Recipeas</Navbar.Brand>
      {isAuthenticated ? (
        <CreateButton />
      ) : (
        <span>Sign up to add your own recipes</span>
      )}
      {isAuthenticated ? (
        <Button onClick={signOut} variant="outline-info">
          Sign Out
        </Button>
      ) : (
        <Button onClick={()=>{
          dispatch(setSignIn())
        }}variant="outline-info">Sign Up</Button>
      )}
    </Navbar>
  );
};

const firebaseAppAuth = firebaseApp.auth();

const providers = {
  googleProvider: new firebase.auth.GoogleAuthProvider(),
};

export default withFirebaseAuth({
  providers,
  firebaseAppAuth,
})(Header);
