/** @format */

import React from "react";
import { useDispatch } from "react-redux";
import { Button, Card, Col, Row } from "react-bootstrap";
import {
  activateForm,
  activateUrl,
  deactivateUrl,
} from "../../slices/form.slice";
import {
  ButtonGroupContainer,
  LeftButtonContainer,
  RightButtonContainer,
} from "./getting-started.styles";

const GettingStarted = () => {
  const dispatch = useDispatch();
  return (
    <Row className="justify-content-center">
      <Col md={4}>
        <Card className="text-center">
          <Card.Header>Getting Started</Card.Header>
          <Card.Body>
            <Card.Title>Create New Recipe</Card.Title>
            <Card.Text>Get started by creating a recipe</Card.Text>
            <ButtonGroupContainer>
              <LeftButtonContainer>
                <Button
                  variant="outline-primary"
                  onClick={() => {
                    dispatch(activateForm());
                    dispatch(activateUrl());
                  }}
                >
                  allrecipes.com
                </Button>
              </LeftButtonContainer>
              <RightButtonContainer>
                <Button
                  variant="outline-dark"
                  onClick={() => {
                    dispatch(activateForm());
                    dispatch(deactivateUrl());
                  }}
                >
                  from scratch
                </Button>
              </RightButtonContainer>
            </ButtonGroupContainer>
          </Card.Body>
        </Card>
      </Col>
    </Row>
  );
};

export default GettingStarted;
