/** @format */

import React from "react";

import { useSelector } from "react-redux";
import {
  selectIsSignIn,
  selectResetPassword,
} from "../../slices/authenticate.slice";
import { Card, Container, Row, Col, Image } from "react-bootstrap";
import SignIn from "../sign-in/sign-in.component";
import SignUp from "../sign-up/sign-up.component";
import ResetPassword from "../reset-password/reset-password.component";
import image from "../../assets/breakfast.jpg";
import { ImageContainer } from "./authenticate.styles";

const Authenticate = () => {
  const isSignIn = useSelector(selectIsSignIn);
  const resetPassword = useSelector(selectResetPassword);

  return (
    <Container
      style={{
        marginTop: "125px",
      }}
    >
      <h4 style={{ textAlign: "center" }}>Create your own recipe collection</h4>
      <Card className="mt-2">
        <Card.Body>
          <Row className="justify-content-center">
            <Col xs={11} sm={10} md={8} lg={6}>
              <ImageContainer>
                <Image fluid src={image} rounded />
              </ImageContainer>
            </Col>
            <Col xs={12} sm={10} md={8} lg={5} xl={4}>
              {!isSignIn ? (
                <SignUp />
              ) : !resetPassword ? (
                <SignIn />
              ) : (
                <ResetPassword />
              )}
            </Col>
          </Row>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default Authenticate;
